package JavaPackage;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age = 25;
		int weight = 50;
		
		if (age > 25) {
			if(weight > 50)
			{
				System.out.println("Eligible");
			}else
			{
				System.out.println("Not eligible");
			}
		}else
		{
			System.out.println("Age is greater than 18");
		}
	}

}
