package JavaPackage;

public class IfElse3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int q = 300;
		int p = 400;
		
		if(q<p)
		{
			System.out.println("q is less than p");
		}else if (q == p) {
			System.out.println("q is equal to p");
		}else {
			System.out.println("q is greater");
		}

	}

}
